import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/categorygrid_item_model.dart';import '../models/userprofilegrid_item_model.dart';import 'package:basri_s_application5/presentation/categories_screen/models/categories_model.dart';part 'categories_event.dart';part 'categories_state.dart';/// A bloc that manages the state of a Categories according to the event that is dispatched to it.
class CategoriesBloc extends Bloc<CategoriesEvent, CategoriesState> {CategoriesBloc(CategoriesState initialState) : super(initialState) { on<CategoriesInitialEvent>(_onInitialize); }

_onInitialize(CategoriesInitialEvent event, Emitter<CategoriesState> emit, ) async  { emit(state.copyWith(categoriesModelObj: state.categoriesModelObj?.copyWith(categorygridItemList: fillCategorygridItemList(), userprofilegridItemList: fillUserprofilegridItemList()))); } 
List<CategorygridItemModel> fillCategorygridItemList() { return [CategorygridItemModel(userImage: ImageConstant.imgRectangle114, userDesignation: "UX Designer"), CategorygridItemModel(userImage: ImageConstant.imgRectangle11448x48, userDesignation: "Web Developer"), CategorygridItemModel(userImage: ImageConstant.imgRectangle1141, userDesignation: "Software Engineer"), CategorygridItemModel(userImage: ImageConstant.imgRectangle1142, userDesignation: "Product Manager")]; } 
List<UserprofilegridItemModel> fillUserprofilegridItemList() { return [UserprofilegridItemModel(userImage: ImageConstant.imgRectangle1143, userName: "Accountant"), UserprofilegridItemModel(userImage: ImageConstant.imgRectangle1144, userName: "Marketing"), UserprofilegridItemModel(userImage: ImageConstant.imgRectangle1145, userName: "App Developer"), UserprofilegridItemModel(userImage: ImageConstant.imgRectangle1146, userName: "Graphic Designer")]; } 
 }
