import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/companyprofile_item_model.dart';import 'package:basri_s_application5/presentation/companies_screen/models/companies_model.dart';part 'companies_event.dart';part 'companies_state.dart';/// A bloc that manages the state of a Companies according to the event that is dispatched to it.
class CompaniesBloc extends Bloc<CompaniesEvent, CompaniesState> {CompaniesBloc(CompaniesState initialState) : super(initialState) { on<CompaniesInitialEvent>(_onInitialize); }

_onInitialize(CompaniesInitialEvent event, Emitter<CompaniesState> emit, ) async  { emit(state.copyWith(companiesModelObj: state.companiesModelObj?.copyWith(companyprofileItemList: fillCompanyprofileItemList()))); } 
List<CompanyprofileItemModel> fillCompanyprofileItemList() { return [CompanyprofileItemModel(userImage: ImageConstant.imgImage4, companyName: "Spotify"), CompanyprofileItemModel(userImage: ImageConstant.imgGoogle, companyName: "Google"), CompanyprofileItemModel(userImage: ImageConstant.imgSpotify, companyName: "Philips"), CompanyprofileItemModel(userImage: ImageConstant.imgFacebook, companyName: "Facebook"), CompanyprofileItemModel(userImage: ImageConstant.imgSpotify44x44, companyName: "HP"), CompanyprofileItemModel(userImage: ImageConstant.imgSpotify1, companyName: "Pinterest")]; } 
 }
